import React from "react";

export default function ResetPasscode() {
  const setnewpswd = (event) => {
    event.preventDefault();
    const currentPswd = document.getElementById("email");
    const newPswd = document.getElementById("password");
    const currentError = document.getElementById("emailError");
    const newError = document.getElementById("passwordError");
    currentError.innerHTML = "";
    newError.innerHTML = "";
    currentPswd.style.borderColor = "#4f8a82";
    newPswd.style.borderColor = "#4f8a82";
    if (currentPswd.value === "" || currentPswd.value === null) {
      currentPswd.style.borderColor = "red";
      currentError.innerHTML = "Current Password is required!";
    }
    if (newPswd.value === "" || newPswd.value === null) {
      newPswd.style.borderColor = "red";
      newError.innerHTML = "New Password is required!";
    }
    if (
      currentPswd.value !== "" &&
      password.value !== "" &&
      password.value === repeat.value
    ) {
      userSignup("https://test.clitehd.com/api/register", {
        email: email.value,
        password: repeat.value,
      }).then((data) => {
        if (data === 400) {
          document.getElementById("email").style.borderColor = "red";
          emailError.innerHTML = "User with this email already exists!";
        } else {
          window.location = "/login";
        }
      });
    }
  };

  return (
    <div>
      <div id="new-pass-container" className="center center-login">
        <h1>
          <div className="title">Set New Password</div>
        </h1>
        <form action="#">
          <div className="text_field">
            <label
              className="email-login email current-password"
              htmlFor="password"
            >
              Current Password
            </label>
            <br />
            <input
              type="password"
              id="email"
              placeholder="Enter Current Password"
              required
            />
          </div>
          <p style={{ color: "red" }} id="emailError"></p>
          <div className="text_field">
            <label
              className="password-login password new-password"
              htmlFor="password"
            >
              New Password
            </label>
            <br />
            <input
              type="password"
              id="password"
              placeholder="Enter New Password"
              required
            />
          </div>
          <p style={{ color: "red" }} id="passwordError"></p>
          <button
            className="login"
            type="submit"
            onClick={(event) => setnewpswd(event)}
          >
            Submit
          </button>
        </form>
      </div>
    </div>
  );
}
