import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import NavBar from "./NavBar";

export default function SignIn() {
  const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;

  async function userLogin(url = "", data = {}) {
    const response = await fetch(url, {
      method: "POST",
      mode: "cors",
      cache: "no-cache",
      credentials: "same-origin",
      headers: {
        "Content-Type": "application/json",
      },
      redirect: "follow",
      referrerPolicy: "no-referrer",
      body: JSON.stringify(data),
    });
    return {
      status: response.status,
      body: await response.json(),
    };
  }

  const login = (event) => {
    event.preventDefault();
    const email = document.getElementById("email");
    const password = document.getElementById("password");
    const emailError = document.getElementById("emailError");
    const passwordError = document.getElementById("passwordError");
    emailError.innerHTML = "";
    passwordError.innerHTML = "";
    if (email.value === "" || email.value === null) {
      emailError.innerHTML = "Email is required!";
    } else if (!regex.test(email.value)) {
      emailError.innerHTML = "This is not a valid email format!";
    }
    if (password.value === "" || password.value === null) {
      passwordError.innerHTML = "Password is required!";
    }
    if (regex.test(email.value) && password.value !== "") {
      userLogin("https://test.clitehd.com/api/login", {
        email: email.value,
        password: password.value,
      }).then((data) => {
        if (data.status === 400) {
          passwordError.innerHTML = "Email or password is incorrect!";
        } else if (data.status === 401) {
          passwordError.innerHTML = "Email or password is incorrect!";
        } else {
          localStorage.getItem(JSON.stringify(data.body.id));
          console.log(JSON.stringify(data.body.id));
          localStorage.setItem("accessToken", JSON.stringify(data.body.token));
          console.log(localStorage.getItem("accessToken")); // JSON data parsed by `data.json()` call
          window.location = "/home";
        }
      });
    }
  };

  return (
    <div>
      <NavBar />
      <div className="center center-login">
        <h1 className="login-title">Welcome Back</h1>
        <form action="#">
          <div className="text_field">
            <label className="email-login email" htmlFor="email">
              E-mail
            </label>
            <br />
            <input type="email" id="email" placeholder="Enter E-mail" />
          </div>
          <p id="emailError"></p>
          <div className="text_field">
            <label className="password-login password" htmlFor="password">
              Password
            </label>
            <br />
            <input type="password" id="password" placeholder="Enter Password" />
          </div>
          <p id="passwordError"></p>
          <button
            className="login"
            type="submit"
            onClick={(event) => login(event)}
          >
            Login
          </button>
          <br />
          <a className="pass forgot-new">
            <Link className="link" to="/forgot">
              Forgot Password?
            </Link>
          </a>
          <br />
          <a className="signup-link forgot-new">
            <Link className="link" to="/signup">
              New to LiteLess?
            </Link>
          </a>
        </form>
      </div>
    </div>
  );
}
