import React from "react";
import { Link } from "react-router-dom";
import NavBar from "./NavBar";

export default function SignUp() {
  const signup = (event) => {
    event.preventDefault();
    const email = document.getElementById("email");
    const password = document.getElementById("password");
    async function userSignup(url = "", data = {}) {
      const response = await fetch(url, {
        method: "POST",
        mode: "cors",
        cache: "no-cache",
        credentials: "same-origin",
        headers: {
          "Content-Type": "application/json",
        },
        redirect: "follow",
        referrerPolicy: "no-referrer",
        body: JSON.stringify(data),
      });
      // return response.json();
    }

    userSignup("https://test.clitehd.com/api/register", {
      email: email.value,
      password: password.value,
    }).then(() => {
      window.location = "/home";
    });
  };

  const confirm = document.querySelector("#repeat").value;
  confirm.addEventListener("change",)

  const confirmPassword = () => {
    const pswd = document.querySelector("#password").value;
    if (pswd === confirm) {
      alert("success");
    } else {
      alert("fail");
    }
  };

  return (
    <div>
      <NavBar />
      <div className="center center-signup">
        <h1 className="signup-title">Create Account</h1>
        <form id="signup-form" action="#">
          <div className="text_field">
            <label className="email" htmlFor="email">
              E-mail
            </label>
            <br />
            <input
              type="email"
              id="email"
              placeholder="Enter E-mail"
              required
            />
          </div>
          <div className="text_field">
            <label className="password" htmlFor="password">
              Password
            </label>
            <br />
            <input
              type="password"
              id="password"
              placeholder="Enter Password"
              required
            />
          </div>
          <div className="text_field">
            <label className="repeat" htmlFor="repeat">
              Confirm Password
            </label>
            <br />
            <input
              type="password"
              id="repeat"
              placeholder="Repeat Password"
              required
            />
          </div>
          <button
            className="signup"
            type="submit"
            onClick={(event) => signup(event)}
          >
            SignUp
          </button>
          <br />
          <a className="pass forgot-new">
            <Link className="link" to="/login">
              Already have an account?
            </Link>
          </a>
        </form>
      </div>
    </div>
  );
}
