import React from "react";
import { useState, useEffect } from "react/cjs/react.development";

export default function MeetingHistory() {
  const [history, setHistory] = useState([]);
  const admin = localStorage.getItem("admin");
  useEffect(() => {
    getHistory();
  }, []);

  function getHistory() {
    fetch(`https://test.clitehd.com/api/meetinghistory`).then((result) => {
      result.json().then((data) => {
        setHistory(data);
      });
    });
  }

  function handleDelete(roomname) {
    fetch(`https://test.clitehd.com/api/meetinghistory/${roomname}`, {
      method: "DELETE",
    }).then((result) => {
      result.json().then(() => {
        getHistory();
      });
    });
  }

  return (
    <div>
      <table class="styled-table">
        <thead>
          <tr>
            <th>#</th>
            <th>Roomname</th>
            <th>Date and Time</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {history.map((gethistory, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{gethistory.roomname}</td>
              <td>{gethistory.dateandtime}</td>
              {gethistory.isAdmin === 0 ? (
                <td>
                  <button
                    className="btn btn-danger"
                    onClick={() => handleDelete(gethistory.roomname)}
                  >
                    Delete
                  </button>
                </td>
              ) : (
                <td>Nil</td>
              )}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
