import React from "react";
import { useState, useEffect } from "react/cjs/react.development";

export default function UserStatus() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    getUsers();
  }, []);

  function getUsers() {
    fetch(`https://test.clitehd.com/api/admin/users`).then((result) => {
      result.json().then((data) => {
        setUsers(data);
      });
    });
  }

  function handleDelete(roomname) {
    fetch(`https://test.clitehd.com/api/admin/users/${roomname}`, {
      method: "DELETE",
    }).then((result) => {
      result.json().then((data) => {
        window.location = "/history";
      });
    });
  }

  return (
    <div>
      <table class="styled-table">
        <thead>
          <tr>
            <th>#</th>
            <th>Users</th>
            <th>Action</th>
            {localStorage.getItem("admin") === "true"
              ? console.log("admin")
              : console.log("")}
          </tr>
        </thead>
        <tbody>
          {users.map((getusers, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{getusers.Email}</td>
              {getusers.isAdmin === 0 ? (
                <td>
                  <button
                    className="btn btn-danger"
                    onClick={() => handleDelete(getusers.email)}
                  >
                    Delete
                  </button>
                </td>
              ) : (
                console.log("")
              )}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
