import axios from "axios";
import React from "react";
import { useState, useEffect } from "react/cjs/react.development";

export default function UserStatus() {
  const [users, setUsers] = useState([]);
  const admin = localStorage.getItem("admin");

  async function userSignup(url = "", data = {}) {
    const response = await fetch(url, {
      method: "POST",
      mode: "cors",
      cache: "no-cache",
      credentials: "same-origin",
      headers: {
        "Content-Type": "application/json",
      },
      redirect: "follow",
      referrerPolicy: "no-referrer",
      body: JSON.stringify(data),
    });
    return {
      status: response.status,
      body: await response.json(),
    };
  }

  const signup = (event) => {
    event.preventDefault();
    const email = document.getElementById("email");
    const password = document.getElementById("password");
    const repeat = document.getElementById("repeat");
    const emailError = document.getElementById("emailError");
    const passwordError = document.getElementById("passwordError");
    const repeatError = document.getElementById("repeatError");
    emailError.innerHTML = "";
    passwordError.innerHTML = "";
    repeatError.innerHTML = "";
    document.getElementById("email").style.borderColor = "#4f8a82";
    document.getElementById("password").style.borderColor = "#4f8a82";
    document.getElementById("repeat").style.borderColor = "#4f8a82";
    if (email.value === "" || email.value === null) {
      document.getElementById("email").style.borderColor = "red";
      emailError.innerHTML = "Email is required!";
    } else if (!regex.test(email.value)) {
      document.getElementById("email").style.borderColor = "red";
      emailError.innerHTML = "This is not a valid email format!";
    }
    if (password.value === "" || password.value === null) {
      document.getElementById("password").style.borderColor = "red";
      passwordError.innerHTML = "Password is required!";
    } else if (password.value.length < 4) {
      document.getElementById("password").style.borderColor = "red";
      passwordError.innerHTML = "Password must be more than 4 characters!";
    } else if (password.value.length > 10) {
      document.getElementById("password").style.borderColor = "red";
      passwordError.innerHTML =
        "Password cannot exceed more than 10 characters!";
    }
    if (password.value !== repeat.value) {
      document.getElementById("repeat").style.borderColor = "red";
      repeatError.innerHTML = "Password does not match!";
    }
    if (
      regex.test(email.value) &&
      password.value !== "" &&
      password.value === repeat.value
    ) {
      userSignup("https://test.clitehd.com/api/register", {
        email: email.value,
        password: password.value,
      }).then((data) => {
        if (data.status === 400) {
          emailError.innerHTML = "Email already exists!";
        } else {
          localStorage.setItem("accessToken", JSON.stringify(data.body.token));
          console.log(localStorage.getItem("accessToken")); // JSON data parsed by `data.json()` call
          window.location = "/home";
        }
      });
    }
  };


  function getUsers() {
    fetch(`https://test.clitehd.com/api/admin/users`).then((result) => {
      result.json().then((data) => {
        setUsers(data);
      });
    });
  }

  function handleDelete(uid) {
    fetch(`https://test.clitehd.com/api/admin/users/${uid}`, {
      method: "DELETE",
    }).then((result) => {
      result.json().then(() => {
        getUsers();
      });
    });
  }

  useEffect(() => {
    getUsers();
  }, []);

  return (
    <div>
      <form onSubmit={submitUser} className="row g-3 mt-4">
        <div className="col-md-3">
          <label className="form-label">Email</label>
          <input
            type="email"
            name="email"
            id="email"
            className="form-control p-2"
            onChange={(e) => handleEmail(e)}
          ></input>
        </div>
        <div className="col-md-3">
          <label className="form-label">Password</label>
          <input
            type="password"
            name="password"
            className="form-control p-2"
            onChange={(e) => handlePassword(e)}
          ></input>
        </div>
        <div className="col-md-3">
          <button
            type="submit"
            className="btn btn-success"
            onClick={(event) => signup(event)}
          >
            Add
          </button>
        </div>
      </form>
      <table className="styled-table">
        <thead>
          <tr>
            <th>#</th>
            <th>Users</th>
            {admin === "1" ? <th>Action</th> : null}
            {admin === "0" ? <th>Role</th> : null}
          </tr>
        </thead>
        <tbody>
          {users.map((getusers, index) => (
            <tr key={getusers.UserId}>
              <td>{index + 1}</td>
              <td>
                {getusers.Email.substring(0, getusers.Email.lastIndexOf("@"))}
              </td>
              {admin === "1" ? (
                getusers.isAdmin === 0 ? (
                  <td>
                    <button
                      className="btn btn-danger"
                      onClick={() => handleDelete(getusers.UserId)}
                    >
                      Delete
                    </button>
                  </td>
                ) : null
              ) : null}
              {admin === "0" ? (
                getusers.isAdmin === 0 ? (
                  <td>User</td>
                ) : (
                  <td>Admin</td>
                )
              ) : null}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
