import React from "react";
import { Typewriter } from "react-simple-typewriter";
import NavBar from "./NavBar";

export default function Home() {
  const joinMeeting = (event) => {
    event.preventDefault();
    const room = document.getElementById("room-name");
    if (localStorage.getItem("accessToken") === null) {
      alert("You must be logged in to join a meeting!");
    } else {
      window.location =
        "https://test.clitehd.com/" +
        room.value +
        "?jwt=" +
        localStorage.getItem("accessToken");
    }
  };

  return (
    <div>
      <NavBar />
      <div className="demo">
        <div className="text-container">
          <h1>Meeting's Made Easier</h1>
          <p className="typewriting">
            <Typewriter
              loop
              cursor
              cursorStyle="|"
              typespeed={40}
              deleteSpeed={70}
              delaySpeed={1000}
              words={["#tired_of_remembering_room_codes"]}
            />
          </p>
          <form action="/">
            <input
              type="text"
              id="room-name"
              placeholder="Enter Room Name"
              required
            />
            <br />
            <button onClick={(event) => joinMeeting(event)}>Join</button>
          </form>
          {localStorage.getItem("accessToken") !== null ? (
            <div className="options">
              <button
                className="create-btn"
                onClick={() => (window.location = "/create")}
              >
                Create
              </button>
              <button onClick={() => (window.location = "/schedule")}>
                Schedule
              </button>
            </div>
          ) : (
            console.log("aisbciahs")
          )}
        </div>
      </div>
    </div>
  );
}
