import React from "react";

export default function ResetPasscode() {
  // const loginPassword = localStorage.getItem("loginPassword");

  const setnewpswd = (event) => {
    event.preventDefault();
    const currentPswd = document.getElementsById("currentPassword");
    const newPswd = document.getElementsById("newPassword");
    const currentError = document.getElementById("emailError");
    const newError = document.getElementById("passwordError");
    currentError.innerText = "";
    newError.innerHTML = "";
    document.querySelector(".getElementsById").style.borderColor = "#4f8a82";
    document.querySelector(".newPassword").style.borderColor = "#4f8a82";
    if (currentPswd.value === "" || currentPswd.value === null) {
      document.getElementsByClassName("currentPassword").style.borderColor =
        "red";
      currentError.innerHTML = "Current Password is required!";
    }
    if (newPswd.value === "" || newPswd.value === null) {
      document.getElementsByClassName("newPassword").style.borderColor = "red";
      newError.innerHTML = "New Password is required!";
    } else if (currentPswd.value !== newPswd.value) {
      document.getElementsByClassName("newPassword").style.borderColor = "red";
      newError.innerHTML = "New Password does not match!";
    }
  };

  return (
    <div>
      <div id="new-pass-container" className="center center-login">
        <h1>
          <div className="title">Set New Password</div>
        </h1>
        <form action="#">
          <div className="text_field">
            <label
              className="email-login email current-password"
              htmlFor="password"
            >
              Current Password
            </label>
            <br />
            <input
              type="password"
              id="currentPassword"
              placeholder="Enter Current Password"
            />
          </div>
          <p style={{ color: "red" }} id="emailError"></p>
          <div className="text_field">
            <label
              className="password-login password new-password"
              htmlFor="password"
            >
              New Password
            </label>
            <br />
            <input
              type="password"
              id="newPassword"
              placeholder="Enter New Password"
            />
          </div>
          <p style={{ color: "red" }} id="passwordError"></p>
          <button
            className="login"
            type="submit"
            onClick={(event) => setnewpswd(event)}
          >
            Submit
          </button>
        </form>
      </div>
    </div>
  );
}
