import React from "react";
import { useState, useEffect } from "react/cjs/react.development";

export default function UserStatus() {
  const [users, setUsers] = useState([]);
  const admin = localStorage.getItem("admin");

  useEffect(() => {
    getUsers();
  }, []);

  function getUsers() {
    fetch(`https://test.clitehd.com/api/admin/users`).then((result) => {
      result.json().then((data) => {
        setUsers(data);
      });
    });
  }

  function handleDelete(uid) {
    fetch(`https://test.clitehd.com/api/admin/users/${uid}`, {
      method: "DELETE",
    }).then((result) => {
      result.json().then(() => {
        getUsers();
      });
    });
  }

  return (
    <div>
      <table class="styled-table">
        <thead>
          <tr>
            <th>#</th>
            <th>Users</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {users.map((getusers) => (
            <tr key={getusers.UserId}>
              <td>{getusers.UserId}</td>
              <td>{getusers.Email}</td>
              {admin !== "0" ? (
                getusers.admin === "0" ? (
                  <td>
                    <button
                      className="btn btn-danger"
                      onClick={() => handleDelete(getusers.UserId)}
                    >
                      Delete
                    </button>
                  </td>
                ) : (
                  <td>Nil</td>
                )
              ) : (
                <td>Nil</td>
              )}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
