import React from "react";
import { Link } from "react-router-dom";
import NavBar from "./NavBar";

export default function SignIn() {
  const initialValues = { email: "", password: ""};
  const [formValues, setFormValues] = useState(initialValues);
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setFormErrors(validate(formValues));
    setIsSubmit(true);
  };

  useEffect(() => {
    console.log(formErrors);
    if (Object.keys(formErrors).length === 0 && isSubmit) {
      console.log(formValues);
    }
  }, [formErrors]);
  const validate = (values) => {
    const errors = {};
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;
    if (!values.username) {
      errors.username = "Username is required!";
    }
    if (!values.email) {
      errors.email = "Email is required!";
    } else if (!regex.test(values.email)) {
      errors.email = "This is not a valid email format!";
    }
    if (!values.password) {
      errors.password = "Password is required";
    } else if (values.password.length < 4) {
      errors.password = "Password must be more than 4 characters";
    } else if (values.password.length > 10) {
      errors.password = "Password cannot exceed more than 10 characters";
    }
    return errors;
  };

  const login = (event) => {
    event.preventDefault();
    const email = document.getElementById("email");
    const password = document.getElementById("password");
    async function userLogin(url = "", data = {}) {
      const response = await fetch(url, {
        method: "POST",
        mode: "cors",
        cache: "no-cache",
        credentials: "same-origin",
        headers: {
          "Content-Type": "application/json",
        },
        redirect: "follow",
        referrerPolicy: "no-referrer",
        body: JSON.stringify(data),
      });
      return response.json();
    }

    userLogin("https://test.clitehd.com/api/login", {
      email: email.value,
      password: password.value,
    }).then((data) => {
      if (data === 400) {
        alert("email does not exist");
      } else {
        localStorage.setItem("accessToken", data.token);
        console.log(localStorage.getItem("accessToken")); // JSON data parsed by `data.json()` call
        window.location = "/home";
      }
    });
  };

  return (
    <div>
      <NavBar />
      <div className="center center-login">
        <h1 className="login-title">Welcome Back</h1>
        <form action="#">
          <div className="text_field">
            <label className="email-login email" htmlFor="email">
              E-mail
            </label>
            <br />
            <input
              type="email"
              id="email"
              placeholder="Enter E-mail"
              required
            />
          </div>
          <div className="text_field">
            <label className="password-login password" htmlFor="password">
              Password
            </label>
            <br />
            <input
              type="password"
              id="password"
              placeholder="Enter Password"
              required
            />
          </div>
          <button
            className="login"
            type="submit"
            onClick={(event) => login(event)}
          >
            Login
          </button>
          <br />
          <a className="pass forgot-new">
            <Link className="link" to="/forgot">
              Forgot Password?
            </Link>
          </a>
          <br />
          <a className="signup-link forgot-new">
            <Link className="link" to="/signup">
              New to LiteLess?
            </Link>
          </a>
        </form>
      </div>
    </div>
  );
}
