import React from "react";
import { useState, useEffect } from "react/cjs/react.development";

export default function MeetingHistory() {
  const [history, setHistory] = useState([]);
  const uid = localStorage.getItem("uid");
  const admin = localStorage.getItem("admin");
  useEffect(() => {
    getHistory();
  }, []);

  const getHistory = async () => {
    fetch(
      `https://test.clitehd.com/api/meetinghistory/${admin === "1" ? "" : uid}`
    ).then((result) => {
      result.json().then((data) => {
        setHistory(data);
      });
    });
  };

  const handleDelete = async (roomname) => {
    console.log(roomname);
    fetch(`https://test.clitehd.com/api/meetinghistory/${roomname}`, {
      method: "DELETE",
    }).then((result) => {
      result.json().then((data) => {
        getHistory();
      });
    });
  };

  return (
    <div>
      <table className="styled-table">
        <thead>
          <tr>
            <th>#</th>
            <th>Roomname</th>
            <th>Date and Time</th>
            {admin === "1" ? <th>Action</th> : null}
          </tr>
        </thead>
        <tbody>
          {history.map((gethistory, index) => (
            <tr key={gethistory.roomname}>
              <td>{index + 1}</td>
              <td>{gethistory.roomname}</td>
              <td>{gethistory.dateandtime}</td>
              {admin === "1" ? (
                <td>
                  <button
                    className="btn btn-danger"
                    onClick={() => handleDelete(gethistory.roomname)}
                  >
                    Delete
                  </button>
                </td>
              ) : null}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
