import React from "react";
import { useState, useEffect } from "react/cjs/react.development";

export default function UserStatus() {
  const [users, setUsers] = useState([]);
  const [email, setEmail] = useState([]);
  const [password, setPassword] = useState([]);
  const admin = localStorage.getItem("admin");

  function handleEmail(e) {
    const email = e.target.value;
    setEmail(email);
  }
  function handlePassword(e) {
    const password = e.target.value;
    setPassword(password);
  }

  function submitUser(e) {
    e.preventDefault();
  }

  function getUsers() {
    fetch(`https://test.clitehd.com/api/admin/users`).then((result) => {
      result.json().then((data) => {
        setUsers(data);
      });
    });
  }

  function handleDelete(uid) {
    fetch(`https://test.clitehd.com/api/admin/users/${uid}`, {
      method: "DELETE",
    }).then((result) => {
      result.json().then(() => {
        getUsers();
      });
    });
  }

  useEffect(() => {
    getUsers();
  }, []);

  return (
    <div>
      <form onSubmit={submitUser} className="row g-3 mt-4">
        <div className="col-md-3">
          <label className="form-label">Email</label>
          <input
            type="email"
            name="email"
            className="form-control p-2"
            onChange={(e) => handleEmail(e)}
          ></input>
        </div>
        <div className="col-md-3">
          <label className="form-label">Password</label>
          <input
            type="password"
            name="password"
            className="form-control p-2"
            onChange={(e) => handlePassword(e)}
          ></input>
        </div>
        <div className="col-md-3">
          <button type="submit" className="btn btn-success">
            Add
          </button>
        </div>
      </form>
      <table class="styled-table">
        <thead>
          <tr>
            <th>#</th>
            <th>Users</th>
            {admin === "1" ? <th>Action</th> : null}
            {admin === "0" ? <th>Role</th> : null}
          </tr>
        </thead>
        <tbody>
          {users.map((getusers, index) => (
            <tr key={getusers.UserId}>
              <td>{index + 1}</td>
              <td>
                {getusers.Email.substring(0, getusers.Email.lastIndexOf("@"))}
              </td>
              {admin === "1" ? (
                getusers.isAdmin === 0 ? (
                  <td>
                    <button
                      className="btn btn-danger"
                      onClick={() => handleDelete(getusers.UserId)}
                    >
                      Delete
                    </button>
                  </td>
                ) : null
              ) : null}
              {admin === "0" ? (
                getusers.isAdmin === 0 ? (
                  <td>User</td>
                ) : (
                  <td>Admin</td>
                )
              ) : null}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
