import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import NavBar from "./NavBar";

export default function SignUp() {
  const initialValues = { email: "", password: "", repeat: "" };
  const [formValues, setFormValues] = useState(initialValues);
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setFormErrors(validate(formValues));
    setIsSubmit(true);
  };

  useEffect(() => {
    console.log(formErrors);
    if (Object.keys(formErrors).length === 0 && isSubmit) {
      console.log(formValues);
    }
  }, [formErrors]);
  const validate = (values) => {
    const errors = {};
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;
    if (!values.username) {
      errors.username = "Username is required!";
    }
    if (!values.email) {
      errors.email = "Email is required!";
    } else if (!regex.test(values.email)) {
      errors.email = "This is not a valid email format!";
    }
    if (!values.password) {
      errors.password = "Password is required";
    } else if (values.password.length < 4) {
      errors.password = "Password must be more than 4 characters";
    } else if (values.password.length > 10) {
      errors.password = "Password cannot exceed more than 10 characters";
    }
    return errors;
  };

  const signup = (event) => {
    event.preventDefault();
    const email = document.getElementById("email");
    const password = document.getElementById("password");
    async function userSignup(url = "", data = {}) {
      const response = await fetch(url, {
        method: "POST",
        mode: "cors",
        cache: "no-cache",
        credentials: "same-origin",
        headers: {
          "Content-Type": "application/json",
        },
        redirect: "follow",
        referrerPolicy: "no-referrer",
        body: JSON.stringify(data),
      });
      return response.status;
    }

    userSignup("https://test.clitehd.com/api/register", {
      email: email.value,
      password: password.value,
    }).then((data) => {
      if (data === 400) {
        alert("email already exists");
      } else {
        localStorage.setItem("accessToken", data.token);
        console.log(localStorage.getItem("accessToken")); // JSON data parsed by `data.json()` call
        window.location = "/home";
      }
    });
  };

  return (
    <div>
      <NavBar />
      <div className="center center-signup">
        <h1 className="signup-title">Create Account</h1>
        <form id="signup-form" action="#">
          <div className="text_field">
            <label className="email" htmlFor="email">
              E-mail
            </label>
            <br />
            <input type="email" id="email" placeholder="Enter E-mail"               value={formValues.email}
              onChange={handleChange}
/>
          </div>
          <div className="text_field">
            <label className="password" htmlFor="password">
              Password
            </label>
            <br />
            <input
              type="password"
              id="password"
              placeholder="Enter Password"
              value={formValues.email}
              onChange={handleChange}
            />
          </div>
          <div className="text_field">
            <label className="repeat" htmlFor="repeat">
              Confirm Password
            </label>
            <br />
            <input type="password" id="repeat" placeholder="Repeat Password" />
          </div>
          <button
            className="signup"
            type="submit"
            onClick={(event) => signup(event)}
          >
            SignUp
          </button>
          <br />
          <a className="pass forgot-new">
            <Link className="link" to="/login">
              Already have an account?
            </Link>
          </a>
        </form>
      </div>
    </div>
  );
}
