import React from "react";
import { useState, useEffect } from "react/cjs/react.development";

export default function MeetingHistory() {
  const [history, setHistory] = useState([]);
  const [message, setMessage] = useState("");
  const uid = localStorage.getItem("uid");
  useEffect(() => {
    const getHistory = async () => {
      const res = await fetch(
        `https://test.clitehd.com/api/meetinghistory/${uid}`
      );
      const getdata = await res.json();
      setHistory(getdata);
    };
    getHistory();
  }, []);

  const handleDelete = async (roomname) => {
    console.log(roomname);
    fetch(`https://test.clitehd.com/api/meetinghistory/${roomname}`).then(
      (result) => {
        result.json().then((data) => {
          setMessage(data);
        });
      }
    );
  };

  return (
    <div>
      <table class="styled-table">
        <thead>
          <tr>
            <th>#</th>
            <th>Roomname</th>
            <th>Date and Time</th>
            <th>Action</th>
            {localStorage.getItem("admin") === "true"
              ? console.log("admin")
              : console.log("")}
          </tr>
        </thead>
        <tbody>
          {history.map((gethistory, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{gethistory.roomname}</td>
              <td>{gethistory.dateandtime}</td>
              <td>
                <button
                  className="btn btn-danger"
                  onClick={() => handleDelete(gethistory.roomname)}
                >
                  Delete
                </button>
              </td>
              {gethistory.isadmin === 0 ? console.log("") : console.log("")}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
