import React from "react";
import NavBar from "./NavBar";

export default function Forgot() {
  const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;
  const forgot = (event) => {
    event.preventDefault();
    const email = document.getElementById("email");
    const password = document.getElementById("password");
    const emailError = document.getElementById("emailError");
    const passwordError = document.getElementById("passwordError");
    emailError.innerHTML = "";
    passwordError.innerHTML = "";
    if (email.value === "" || email.value === null) {
      emailError.innerHTML = "Email is required!";
    } else if (!regex.test(email.value)) {
      emailError.innerHTML = "This is not a valid email format!";
    }
    if (password.value === "" || password.value === null) {
      passwordError.innerHTML = "Password is required!";
    }
    if (regex.test(email.value) && password.value !== "") {
      userLogin("https://test.clitehd.com/api/login", {
        email: email.value,
        password: password.value,
      }).then((data) => {
        if (data === 400) {
          passwordError.innerHTML = "Email or password is incorrect!";
        } else if (data === 401) {
          passwordError.innerHTML = "Email or password is incorrect!";
        } else {
          localStorage.setItem("accessToken", data.token);
          // console.log(localStorage.getItem("accessToken")); // JSON data parsed by `data.json()` call
          window.location = "/home";
        }
      });
    }
  };

  return (
    <div>
      <NavBar />
      <div className="center center-login">
        <h1>
          <div className="title">
            Letâ€™s check if your E-mail is in our bucket :{")"}
          </div>
        </h1>
        <form action="#">
          <div className="text_field">
            <label className="email-login email" htmlFor="username">
              E-mail
            </label>
            <br />
            <input
              type="email"
              id="email"
              placeholder="Enter E-mail"
              required
            />
          </div>
          <button className="login" type="submit">
            Submit
          </button>
        </form>
      </div>
    </div>
  );
}
