import React from "react";
import { useState, useEffect } from "react/cjs/react.development";

export default function UserStatus() {
  const [users, setUsers] = useState([]);
  const [message, setMessage] = useState("");

  useEffect(() => {
    const getUsers = async () => {
      const res = await fetch("https://test.clitehd.com/api/admin/users");
      const getdata = await res.json();
      setUsers(getdata);
    };
    getUsers();
  }, []);

  const handleDelete = async (email) => {
    console.log(email);
    const reqDelete = await fetch(
      `https://test.clitehd.com/api/login/${email}`
    );
    const resDelete = await reqDelete.json();
    setMessage(resDelete.message);
  };

  return (
    <div>
      <table class="styled-table">
        <thead>
          <tr>
            <th>#</th>
            <th>Users</th>
            <th>Action</th>
            {localStorage.getItem("admin") === "true"
              ? console.log("admin")
              : console.log("")}
          </tr>
        </thead>
        <tbody>
          {users.map((getusers, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{getusers.Email}</td>
              {getusers.isAdmin === 1 ? (
                <td>
                  <button
                    className="btn btn-danger"
                    onClick={() => handleDelete(getusers.email)}
                  >
                    Delete
                  </button>
                </td>
              ) : (
                console.log("")
              )}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
